function addInput(e){if(counter==limit)alert("You have reached the limit of adding "+counter+" inputs");else{var n=document.createElement("div");n.innerHTML="<br><form method='POST'><div class='col-sm-7'><input type='text' class='form-control' name='myInputs[]' id='ingredientName' placeholder='Enter ingredient name'></div><div class='col-sm-5'><input type='number' class='form-control' id='ingredientNumber' placeholder='How many'></div></form>",document.getElementById(e).appendChild(n),counter++}}function incrementIngredientAmount(){ingredientAmount=0,document.getElementById(buttonName).innerHTML=ingredientAmount+=1}function Ingredient(e){this.name=e,this.count=1}function Recipe(e,n,t){this.name=e,this.requirements=n,this.instructions=t}function addIngredient(e){pantry.ingredients.push(new Ingredient(e))}function addRecipe(e,n,t){pantry.recipes.push(new Recipe(e,n,t))}var counter=1,limit=100,pantry=[ingredients=[],recipes=[]];